 	$sql="INSERT into requerimiento_usuarios (usuario,
 	procesos,
 	datefecha,
 	software,
 	direccion,
 	requerimiento,
 	prioridad,
 	objetivorequerimiento,
 	alcancerequerimiento,
 	impactorequerimiento,
 	otrosprocesos)values
 	('$usuario',
